import {
  Streamlit,
  StreamlitComponentBase,
  withStreamlitConnection,
} from "streamlit-component-lib"
import React, { ReactNode, useState } from "react"



import Box from '@material-ui/core/Box'
import Slider from '@material-ui/core/Slider'
import styled from '@material-ui/styles/styled'



function valuetext(value:number) {
  return `${value}°C`;
}


class VerticalSlider extends StreamlitComponentBase{
  public state = { currentValue: 0 }

  public render = (): ReactNode => {
    const hMargin = 10
    const vMargin = 50 
    const StyledSlider = styled(Slider)({
      margin:`${vMargin}px ${hMargin}px`,
      padding: "5px 5px 5px 5px",
      width:this.props.width-(hMargin),
      color:"#29B5E8"
      });

    // function valueLabelFormat(value:number) {
    //   return options.findIndex(options) + 1;
    // }

    function createSteps(labels:string[]): any[] {
      return labels.map((label,index)=>{return{value:index,label}})
    }

    const options = this.props.args["options"]
    const step = this.props.args["step"]
    let default_value = this.props.args["default"]



    return (
      <Box sx={{ height:150}}>
      <StyledSlider
        orientation="vertical"
        getAriaValueText={valuetext}
       // step={step}
        min={0}
        max={100}
        value={this.state.currentValue}
        defaultValue={default_value}
        valueLabelDisplay="auto"
        onChangeCommitted={(event,value) => {  
          this.setState({currentValue:value})
          Streamlit.setComponentValue(value)
        }}
        disabled= {this.props.disabled}
        //marks={createSteps(options)}
      />
    </Box>
    )
  }
  private onChange =(value:number): void =>{
    this.setState({currentValue:value}
    )
  }
  private onChangeCommitted =(value:number): void =>{
    this.setState({currentValue:value},
      () => Streamlit.setComponentValue(value)
    )
  }

} 
export default withStreamlitConnection(VerticalSlider)
