import {
  ComponentProps,
  Streamlit,
  withStreamlitConnection,
} from "streamlit-component-lib"
import React, { ReactNode, useState, useEffect } from "react"
import Box from '@material-ui/core/Box'
import { withStyles, makeStyles } from "@material-ui/styles/";
import { Slider } from "@material-ui/core";

const VerticalSlider = (props: ComponentProps) => {

  const { label, min_value, max_value, default_value, step } = props.args;
  //const [value, setValue] = useState([default_value]);
  useEffect(() => Streamlit.setFrameHeight());
  const handleChange = (event: any, newValue: number | number[]) => {
    Streamlit.setComponentValue(newValue);
  };
  const hMargin = 5

  const useStyles = makeStyles(theme => ({
    margin: {
      margin: theme.spacing(10),
      "& .MuiSlider-thumb": {
        height: 24,
        width: 24
      }
    }
  }));


  const styledSlider = withStyles({
    height: "200px",
    marginLeft: 5,
    marginRight: 5,
    marginTop: 50,
    marginBottom: 50,
    width: props.width - (hMargin),
    color: "#29B5E8",
    rail: {
      height: 24,
      width: "14px !important",
      borderRadius: 24,
      opacity: 1,
    }
  })(Slider)

  return (
    <Box sx={{ height: 150 }}>
      <styledSlider
        min={min_value}
        step={step}
        max={max_value}
        defaultValue={default_value}
        onChange={handleChange}
        valueLabelDisplay="on"
        orientation="vertical"
        aria-labelledby="continuous-slider"
        style={style}
        ThumbComponent="span"
        marks={[{ value: Number(min_value), label: String(min_value) }, { value: Number(max_value), label: String(max_value) }]}
      />
    </Box>

  );

}
export default withStreamlitConnection(VerticalSlider);
