import {
  Streamlit,
  StreamlitComponentBase,
  withStreamlitConnection,
} from "streamlit-component-lib"
import React, { ReactNode } from "react"
import Box from '@material-ui/core/Box'
import Slider from '@material-ui/core/Slider'
import styled from '@material-ui/styles/styled'



const marks = [
  {
    value: 0,
    label: '0°C',
  },
  {
    value: 20,
    label: '20°C',
  },
  {
    value: 37,
    label: '37°C',
  },
  {
    value: 100,
    label: '100°C',
  },
];


function createMarks(labels:string[]): any[] {
    return labels.map((label,index)=> {return{value:index,label}})
}

function valuetext(value:number) {
  return `${value}°C`;
}

function valueLabelFormat(value:number) {
  return marks.findIndex((mark) => mark.value === value) + 1;
}
class VerticalSlider extends StreamlitComponentBase{

  public render = (): ReactNode => {
    const hMargin = 10
    const vMargin = 50 
    const StyledSlider = styled(Slider)({
      margin:`${vMargin}px ${hMargin}px`,
      padding: "5px 5px 5px 5px",
      width:this.props.width-(hMargin),
      color:"#29B5E8"
      });
    const options = this.props.args["options"]
    return (
      <Box sx={{ height: 300, width:200 }}>
      <StyledSlider
        //aria-label="Restricted values"
        //defaultValue={20}
        orientation="vertical"
        valueLabelFormat={valueLabelFormat}
         getAriaValueText={valuetext}
       //step={1}
        max={10}
        min={0}
        valueLabelDisplay="auto"
        marks={createMarks(options)}
      />
    </Box>
    );
  }

} 



export default withStreamlitConnection(VerticalSlider)
